function scrollCarousel(direction) {
    const container = document.querySelector('.carousel');
    const scrollAmount = container.offsetWidth * direction;
    container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
}

document.addEventListener("DOMContentLoaded", function() {
    const box10 = document.getElementById('box10');
    const video = document.getElementById('hover-video');
    const img = box10.querySelector('img');

    box10.addEventListener('mouseenter', function() {
        img.style.display = 'none';
        video.style.display = 'block';
        video.play();

        setTimeout(() => {
            video.style.display = 'none';
            video.pause();
            img.style.display = 'block';
        }, 8000);
    });
});
